import React from 'react';
import { Grid, Header, Dimmer, Loader, Message, Segment, Image, Divider, Menu, Form } from 'semantic-ui-react';
import { authAxios } from '../utils';
import { addressListURL } from '../constants';

class Profile extends React.Component {
    state = {
        activeItem: "billingAddress",
        error: null,
        loading: false,
        addresses: []
    };

    handleItemClick = (name) => {
        this.setState({
            activeItem: name
        });
    }

    hangleFetchAddresses = () => {
        this.setState({ loading: true });

        authAxios
            .get(addressListURL)
            .then(res => {
                this.props.fetchCart();
                this.setState({ addresses: res.data, loading: false });
            })
            .catch(err => {
                this.setState({ error: err });
            });
    };


    render() {
        const { activeItem, error, loading, addresses } = this.state;
        return (
            <Grid container columns={2} divided>
                <Grid.Row columns={1}>
                    <Grid.Column>
                        {error && (
                            <Message
                                error
                                header='There was some errors with your submission'
                                content={JSON.stringify(error)}
                            />
                        )}
                        {
                            loading && (
                                <Segment>
                                    <Dimmer active inverted>
                                        <Loader inverted content='Loading' />
                                    </Dimmer>

                                    <Image src='https://react.semantic-ui.com/images/wireframe/short-paragraph.png' />
                                </Segment>
                            )
                        }
                        {addresses.map(a => {
                            return <div>{a.street_address}</div>;
                        })}
                    </Grid.Column>
                </Grid.Row>
                <Grid.Row>
                    <Grid.Column width={6}>

                        <Menu pointing vertical fluid>
                            <Menu.Item
                                name="Billing Address"
                                active={activeItem === "billingAddress"}
                                onClick={() => this.handleItemClick("billingAddress")}
                            />
                            <Menu.Item
                                name="Shipping Address"
                                active={activeItem === "shippingAddress"}
                                onClick={() => this.handleItemClick("shippingAddress")}
                            />
                        </Menu>

                    </Grid.Column>
                    <Grid.Column width={10}>
                        <Header>
                            {`Update your ${activeItem === "billingAddress" ? "billing" : "shipping"} address`}
                        </Header>
                        <Divider />
                        <Form>
                            <Form.Input name="street_address" placeholder="Street address" />
                            <Form.Input name="appartement_address" placeholder="Appartement address" />
                            <Form.Input name="zip" placeholder="Zip code" />
                            <Form.Input name="street_address" placeholder="Street address" />
                            <Form.Checkbox name="default" label="Make this the default address" />
                            <Form.Button primary>Save</Form.Button>
                        </Form>
                    </Grid.Column>
                </Grid.Row>
            </Grid>
        );
    }
}

export default Profile;
