import React from 'react';
import { Grid, Header, Dimmer, Loader, Message, Segment, Image, Divider, Menu, Form, Select, Card, Label, Button } from 'semantic-ui-react';
import { authAxios } from '../utils';
import { addressListURL, countryListURL, addressCreateURL, userIDURL } from '../constants';
import { cartFail } from '../store/actions/cart';


class AddressForm extends React.Component {
    state = {
        error: null,
        loading: false,
        formData: { default: false },
        saving: false,
        success: false
    };


    handleChange = e => {
        const { formData } = this.state;
        const updateFormData = {
            ...formData,
            [e.target.name]: e.target.value
        };
        this.setState({
            formData: updateFormData
        });
    };


    handleSelectChange = (e, { name, value }) => {
        const { formData } = this.state;
        const updateFormData = {
            ...formData,
            [name]: value
        };
        this.setState({
            formData: updateFormData
        });
    };


    handleToggleDefault = () => {
        const { formData } = this.state;
        const updateFormData = {
            ...formData,
            default: !formData.default
        };
        this.setState({
            formData: updateFormData
        });
    };
    handleCreateAddress = e => {
        this.setState({ saving: true });
        e.preventDefault();
        const { activeItem, formData, userID } = this.state;
        authAxios
            .post(addressCreateURL, {
                ...formData,
                user: userID,
                address_type: activeItem === "billingAddress" ? "B" : "S"
            })
            .then(res => {
                this.setState({ saving: true, success: true });
            })
            .catch(err => {
                this.setState({ error: err });
            });

    };

    render() {
        const { error, success, saving } = this.state;
        const { countries } = this.props;

        return (
            <Form onSubmit={this.handleCreateAddress} success={success} error={error}>
                <Form.Input
                    required
                    name="street_address"
                    placeholder="Street address"
                    onChange={this.handleChange}

                />
                <Form.Input
                    required
                    name="apartment_address"
                    placeholder="Apartment address"
                    onChange={this.handleChange}

                />
                <Form.Field required>
                    <Select
                        loading={countries.length < 1}
                        clearable
                        search
                        options={countries}
                        fluid
                        name="country"
                        placeholder="Country"
                        onChange={this.handleSelectChange}
                    />
                </Form.Field>
                <Form.Input
                    required
                    name="zip"
                    placeholder="Zip code"
                    onChange={this.handleChange}

                />
                <Message
                    success
                    header="Success!"
                    content="Your address was saved"
                />
                {error && (
                    <Message
                        error
                        header='There was an error'
                        content={JSON.stringify(error)}
                    />
                )}
                <Form.Checkbox
                    name="default"
                    label="Make this the default address"
                    onChange={this.handleToggleDefault}
                />
                <Form.Button primary disabled={saving} loading={saving}>Save</Form.Button>

            </Form>
        );
    }
}

class Profile extends React.Component {
    state = {
        activeItem: "billingAddress",
        addresses: [],
        countries: [],
        userID: null
    };

    componentDidMount() {
        this.handleFetchAddresses();
        this.handleFetchCountries();
        this.handleFetchUserID();
    }

    handleItemClick = (name) => {
        // we use a callback on the setState here because we want the activeItem to be set before 
        // the execution of handleFetchAddress().
        // withou that, this.handleFetchAddress() is call before this.setState(). there are in different thread
        this.setState({ activeItem: name }, () => {
            this.handleFetchAddresses();
        });

    }

    handleFormatCountries = countries => {
        const keys = Object.keys(countries);
        return keys.map(k => {
            return {
                key: k,
                text: countries[k],
                value: k
            };
        });
    };


    handleFetchCountries = () => {
        authAxios
            .get(countryListURL)
            .then(res => {
                this.setState({ countries: this.handleFormatCountries(res.data) });
            })
            .catch(err => {
                this.setState({ error: err });
            });
    };

    handleFetchUserID = () => {
        authAxios
            .get(userIDURL)
            .then(res => {
                this.setState({ userID: res.data.userID });
            })
            .catch(err => {
                this.setState({ error: err });
            });
    };

    handleFetchAddresses = () => {
        this.setState({ loading: true });
        const { activeItem } = this.state;
        authAxios
            .get(
                addressListURL(activeItem === "billingAddress" ? "B" : "S")
            )
            .then(res => {
                this.setState({ addresses: res.data, loading: false });
            })
            .catch(err => {
                this.setState({ error: err });
            });
    };




    render() {
        const { activeItem, error, loading, addresses, countries } = this.state;
        return (
            <Grid container columns={2} divided>
                <Grid.Row columns={1}>
                    <Grid.Column>
                        {error && (
                            <Message
                                error
                                header='There was an error'
                                content={JSON.stringify(error)}
                            />
                        )}
                        {
                            loading && (
                                <Segment>
                                    <Dimmer active inverted>
                                        <Loader inverted content='Loading' />
                                    </Dimmer>

                                    <Image src='https://react.semantic-ui.com/images/wireframe/short-paragraph.png' />
                                </Segment>
                            )
                        }
                    </Grid.Column>
                </Grid.Row>
                <Grid.Row>
                    <Grid.Column width={6}>

                        <Menu pointing vertical fluid>
                            <Menu.Item
                                name="Billing Address"
                                active={activeItem === "billingAddress"}
                                onClick={() => this.handleItemClick("billingAddress")}
                            />
                            <Menu.Item
                                name="Shipping Address"
                                active={activeItem === "shippingAddress"}
                                onClick={() => this.handleItemClick("shippingAddress")}
                            />
                            <Menu.Item
                                name="Payment history"
                                active={activeItem === "paymentHistory"}
                                onClick={() => this.handleItemClick("paymentHistory")}
                            />
                        </Menu>

                    </Grid.Column>
                    <Grid.Column width={10}>
                        <Header>
                            {`Update your ${activeItem === "billingAddress" ? "billing" : "shipping"} address`}
                        </Header>
                        <Divider />
                        <Card.Group>
                            {addresses.map(a => {
                                return (
                                    <Card key={a.id}>
                                        <Card.Content>
                                            {a.default && (
                                                <Label as="a" color="blue" ribbon="right">
                                                    Default
                                                </Label>
                                            )}
                                            <Card.Header>
                                                {a.street_address}, {a.apartment_address}
                                            </Card.Header>
                                            <Card.Meta>{a.country}</Card.Meta>
                                            <Card.Description>{a.zip}</Card.Description>
                                        </Card.Content>

                                        <Card.Content extra>
                                            <Button color="yellow">Update</Button>
                                            <Button color="red">Delete</Button>
                                        </Card.Content>

                                    </Card>
                                );
                            })}
                        </Card.Group>
                        {addresses.length > 0 ? <Divider /> : null}

                        <AddressForm countries={countries} addresses={addresses} />

                    </Grid.Column>
                </Grid.Row>
            </Grid>
        );
    }
}

export default Profile;
