import React from 'react';
import { Grid, Header, Divider, Menu, Form } from 'semantic-ui-react';

class Profile extends React.Component {
    state = {
        activeItem: "billingAddress"
    };

    handleItemClick = (e, { name }) => {
        this.setState({
            activeItem: name
        });
    }

    render() {
        const { activeItem } = this.state;
        return (
            <Grid container columns={2} divided>
                <Grid.Row>
                    <Grid.Column width={6}>

                        <Menu pointing vertical fluid>
                            <Menu.Item
                                name="Billing Address"
                                active={activeItem === "billingAddress"}
                                onClick={this.handleItemClick}
                            />
                            <Menu.Item
                                name="Physical Address"
                                active={activeItem === "physicalAddress"}
                                onClick={this.handleItemClick}
                            />
                        </Menu>

                    </Grid.Column>
                    <Grid.Column width={10}>
                        <Header>{`Update your ${activeItem === "billingAddress" ? "billing" : "shipping"}`}</Header>
                        <Divider />
                        <Form>
                            <Form.Input name="street_address" placeholder="Street address" />
                            <Form.Input name="appartement_address" placeholder="Appartement address" />
                            <Form.Input name="zip" placeholder="Zip code" />
                            <Form.Input name="street_address" placeholder="Street address" />
                            <Form.Checkbox name="default" label="Make this the default address" />
                            <Form.Button primary>Save</Form.Button>
                        </Form>
                    </Grid.Column>
                </Grid.Row>
            </Grid>
        );
    }
}

export default Profile;
