import React, { Component } from 'react';
import {
    CardElement,
    injectStripe,
    Elements,
    StripeProvider
} from 'react-stripe-elements';
import { Button, Container, Message } from 'semantic-ui-react';
import { authAxios } from '../utils';
import { checkoutURL } from '../constants';

class CheckoutForm extends Component {

    state = {
        loading: false,
        error: null,
        success: false
    }

    submit = (ev) => {
        ev.preventDefault();
        this.setState({ loading: true });
        if (this.props.stripe) {
            this.props.stripe.createToken()
                .then(result => {
                    if (result.error) {
                        this.setState({ error: result.error.message, loading: false });
                    } else {
                        authAxios
                            .post(checkoutURL, { stripeToken: result.token.id })
                            .then(res => {
                                this.setState({ loading: true, success: true });
                            })
                            .catch(err => {
                                this.setState({ loading: false, error: err });
                            });
                    }
                });
        } else {
            console.log("Stripe is not loaded");
        }

    };

    render() {
        const { error, loading, success } = this.state;
        return (
            <div>
                {error && (
                    <Message negative>
                        <Message.Header>Your payment was unsuccessful</Message.Header>
                        <p>{JSON.stringify(error)}</p>
                    </Message>
                )}
                {success && (
                    <Message positive>
                        <Message.Header>Your payment was successful</Message.Header>
                        <p>
                            Go to your <b>profile</b> to see the order delivery status.
                        </p>
                    </Message>
                )}
                <p> Would you like to complete your puchase?</p>
                <CardElement />
                <Button
                    onClick={this.submit}
                    primary
                    style={{ marginTop: "10px" }}
                    loading={loading}
                    disabled={loading}
                >
                    Submit
                </Button>
            </div>
        );
    }
}


const InjectedForm = injectStripe(CheckoutForm);

const WrappedForm = () => (
    <Container text>
        <StripeProvider apiKey="">
            <div>
                <h1>React Stripe Elements</h1>
                <Elements>
                    <InjectedForm />
                </Elements>

            </div>

        </StripeProvider>
    </Container>

);

export default WrappedForm;
