import React, { Component } from 'react';
import {
    CardElement,
    injectStripe,
    Elements,
    StripeProvider
} from 'react-stripe-elements';
import { Button, Container, Dimmer, Message, Item, Divider, Header, Loader } from 'semantic-ui-react';
import { authAxios } from '../utils';
import { checkoutURL } from '../constants';



class OrderPreview extends Component {

    state = {
        data: null,
        error: null,
        loading: false
    }

    componentDidMount() {
        this.handleFetchOrder();
    }

    handleFetchOrder = () => {
        this.setState({ loading: true });
        authAxios
            .get(orderSummaryURL)
            .then(res => {
                this.setState({ data: res.data, loading: false });
            })
            .catch(err => {
                if (err.reponse.status === 404) {
                    this.setState({ error: "You currently do not have an order", loading: false });
                } else {
                    this.setState({ error: err, loading: false });
                }
            });
    };

    render() {
        const { data, error, loading } = this.state;

        return (
            <div>
                {error && (
                    <Message
                        error
                        header="There was an error"
                        content={JSON.stringify(error)}
                    />
                )}
                {loading && (
                    <Segment>
                        <Dimmer>
                            <Loader inverted>Loading</Loader>
                        </Dimmer>
                    </Segment>
                )}React.Fragment
                {data && (
                    <React.Fragment>
                        <Item.Group relaxed>
                            {data.order_items.map((order_item, i) => {
                                return (
                                    <Item>
                                        <Item.Image
                                            size="tiny"
                                            src=""
                                        />

                                        <Item.Content verticalAlign="middle">
                                            <Item.Header as="a"></Item.Header>
                                        </Item.Content>

                                    </Item>
                                );
                            })}

                        </Item.Group>

                        <Item.Group>
                            <Item.Content>
                                <Item.Header>Order Total: </Item.Header>
                            </Item.Content>
                        </Item.Group>
                    </React.Fragment>
                )}

            </div>
        );
    }
}


class CheckoutForm extends Component {

    state = {
        loading: false,
        error: null,
        success: false
    }

    submit = (ev) => {
        ev.preventDefault();
        this.setState({ loading: true });
        if (this.props.stripe) {
            this.props.stripe.createToken()
                .then(result => {
                    if (result.error) {
                        this.setState({ error: result.error.message, loading: false });
                    } else {
                        authAxios
                            .post(checkoutURL, { stripeToken: result.token.id })
                            .then(res => {
                                this.setState({ loading: true, success: true });
                            })
                            .catch(err => {
                                this.setState({ loading: false, error: err });
                            });
                    }
                });
        } else {
            console.log("Stripe is not loaded");
        }

    };

    render() {
        const { error, loading, success } = this.state;
        return (
            <div>
                {error && (
                    <Message negative>
                        <Message.Header>Your payment was unsuccessful</Message.Header>
                        <p>{JSON.stringify(error)}</p>
                    </Message>
                )}
                {success && (
                    <Message positive>
                        <Message.Header>Your payment was successful</Message.Header>
                        <p>
                            Go to your <b>profile</b> to see the order delivery status.
                        </p>
                    </Message>
                )}


                <OrderPreview />


                <Divider />

                <Header>Would you like to complete your puchase?</Header>
                <CardElement />
                <Button
                    onClick={this.submit}
                    primary
                    style={{ marginTop: "10px" }}
                    loading={loading}
                    disabled={loading}
                >
                    Submit
                </Button>
            </div>
        );
    }
}


const InjectedForm = injectStripe(CheckoutForm);


const WrappedForm = () => (
    <Container text>
        <StripeProvider apiKey="pk_test_ffJsfC2lo7brg48q4ijmDjK000UJ9rB62K">
            <div>
                <h1>Complete your order</h1>
                <Elements>
                    <InjectedForm />
                </Elements>

            </div>

        </StripeProvider>
    </Container>

);

export default WrappedForm;
